INSERT INTO `zhcn` (`id`,`desc`) VALUES ('140031','|<#253,208,110#>概述：||<#\n#>|击破城门|<#72,255,98#>3|，可以开启|<#72,255,98#>区域5|的战争迷雾。并且获得|<#72,255,98#>区域5|内的复活点|<#\n#>| |<#\n#>||<#253,208,110#>如何造成伤害：||<#\n#>||<#59,108,247#>玩家派兵|攻打会对城门|<#72,255,98#>3|造成伤害|<#\n#>| |<#\n#>||<#253,208,110#>血量：||<#\n#>|根据守方玩家|<#59,108,247#>总府衙等级|计算，等级越高血量越多。|<#\n#>| |<#\n#>||<#253,208,110#>摧毁后影响：||<#\n#>|城门|<#72,255,98#>3|被摧毁后，|<#72,255,98#>区域5|的|<#59,108,247#>战争迷雾消失|，攻方可以|<#59,108,247#>迁城|前往|<#72,255,98#>区域5，|可攻击内城');
INSERT INTO `zhcn` (`id`,`desc`) VALUES ('140032','|<#253,208,110#>概述：||<#\n#>|守军占领可以攻击|<#59,108,247#>攻城锤|。|<#\n#>| |<#\n#>||<#253,208,110#>使用权限：||<#\n#>||<#59,108,247#>防守方|可以使用。|<#\n#>| |<#\n#>||<#253,208,110#>驻守人数：||<#\n#>|最多|<#72,255,98#>1|人。|<#\n#>| |<#\n#>||<#253,208,110#>伤害值：||<#\n#>|根据驻守进入的|<#59,108,247#>军团战斗力|调整。战斗力越高对攻城锤造成伤害越高。|<#\n#>| |<#\n#>||<#253,208,110#>攻击间隔：||<#\n#>|当有玩家驻守床弩时，床弩每|<#72,255,98#>30|秒对攻城锤造成一次伤害。|<#\n#>| |<#\n#>||<#253,208,110#>血量：||<#\n#>|床弩没有血量，也不会成为被攻击目标');
