INSERT INTO `zhcn` (`id`,`desc`) VALUES ('331002','【士兵等级】士兵|<#225,40,50#>等级越高战力越强，兵种类型分为8大类|。|<#\n#>|盾兵-高防/高血/克制弓兵|<#\n#>|枪兵-高攻/高暴/克制骑兵|<#\n#>|骑兵-高移速/闪避/克制远程|<#\n#>|弓骑-高移速/剿匪战加成/克制远程|<#\n#>|弓兵-高射速/高攻|<#\n#>|弩兵-射程远/守城加成|<#\n#>|冲车-高负重/城战加成/免疫陷阱|<#\n#>|投石-高攻/城战加成/免疫陷阱');
INSERT INTO `zhcn` (`id`,`desc`) VALUES ('331003','【武将信物】击败野外怪物或营寨可获得|<#225,40,50#>武将信物|。|<#\n#>|【招募方式】集齐所需|<#225,40,50#>武将信物|可在|<#225,40,50#>酒馆|招募该武将。|<#\n#>|【武将作用】在|<#225,40,50#>校场编辑|军团中编辑|<#225,40,50#>武将上阵|可有效提升士兵的战斗力。|<#\n#>|【武将驻守】武将可|<#225,40,50#>驻守|在建筑中为建筑|<#225,40,50#>提升效果|，驻守在|<#225,40,50#>城墙|上的武将会对|<#225,40,50#>所有预备役加成|。|<#\n#>|【武将属性】共有5种属性，所有属性根据不同|<#225,40,50#>建筑需求|影响|<#225,40,50#>驻守建筑的效果|。|<#\n#>||<#225,40,50#>武力|-增加武将携带部队的|<#225,40,50#>攻击力|（武力与智力取最高值）|<#\n#>||<#22,155,209#>智力|-增加武将携带部队的|<#225,40,50#>攻击力|（武力与智力取最高值）|<#\n#>||<#72,255,98#>政治|-影响驻守建筑时的|<#225,40,50#>增益效果||<#\n#>||<#255,126,0#>统帅|-增加武将携带部队的|<#225,40,50#>生命值||<#\n#>||<#167,85,230#>魅力|-影响驻守建筑时的|<#225,40,50#>增益效果|');
INSERT INTO `zhcn` (`id`,`desc`) VALUES ('331004','【天赋获取】|<#225,40,50#>主公升级|可获得|<#225,40,50#>天赋点数|。|<#\n#>|【天赋学习】使用|<#225,40,50#>天赋点数|可以学习天赋。|<#\n#>|【天赋类型】分为|<#225,40,50#>战争|/|<#22,155,209#>建设|/|<#72,255,98#>军备|三大系|<#\n#>||<#225,40,50#>战争系|-对战斗相关的增益|<#\n#>||<#22,155,209#>建设系|-对建筑建造，科技研究，资源采集等的增益|<#\n#>||<#72,255,98#>军备系|-对训练士兵，陷阱，行军速度等的增益');
INSERT INTO `zhcn` (`id`,`desc`) VALUES ('331005','宝物并不能直接提升战力，但是宝物提供的各种增益效果能让建筑效果、士兵能力获得显著提高。|<#\n#>|击杀匪徒是获得宝物的唯一途径。');
INSERT INTO `zhcn` (`id`,`desc`) VALUES ('331006','【功能开启】|<#225,40,50#>府衙达到3级|后可建造|<#225,40,50#>铁匠铺||<#\n#>|【装备属性】所有装备有|<#225,40,50#>武|/|<#22,155,209#>智|/|<#72,255,98#>政|/|<#255,126,0#>统|/|<#167,85,230#>魅|5种属性和特技，特技分为|<#225,40,50#>军事|和|<#225,40,50#>内政|两种|<#\n#>|【获取途径】|<#225,40,50#>剿匪|/|<#225,40,50#>攻打营寨|可获得装备|<#\n#>|【装备进阶】集齐|<#225,40,50#>进阶材料|和集齐|<#225,40,50#>万能装备|消耗白银可提升装备的5属性和特技加成|<#\n#>|【装备重铸】消耗元宝可返还|<#225,40,50#>所有进阶的材料|和|<#225,40,50#>万能装备||<#\n#>|【装备分解】获得1件同品质|<#225,40,50#>万能装备|和|<#225,40,50#>白银|');
INSERT INTO `zhcn` (`id`,`desc`) VALUES ('331007','【功能开启】|<#225,40,50#>府衙达到4级|后可建造|<#225,40,50#>研究所||<#\n#>|【科技类型】可分为|<#225,40,50#>军事|/|<#225,40,50#>内政|科技2种|<#\n#>||<#\n#>|军事科技 - |<#225,40,50#>提升部队的战斗能力||<#\n#>|内政科技 - |<#225,40,50#>提升资源的产出，获取效率|');
INSERT INTO `zhcn` (`id`,`desc`) VALUES ('331008','【功能开启】|<#225,40,50#>府衙达到4级|后可建造战争工坊|<#\n#>|【陷阱种类】陷阱在守城时可被|<#225,40,50#>概率触发|，不同陷阱对应不同兵种，|<#225,40,50#>车兵不触发陷阱||<#\n#>||<#\n#>|滚木 - 克制|<#225,40,50#>弓兵||<#\n#>|飞矢 - 克制|<#225,40,50#>骑兵||<#\n#>|落石 - 克制|<#225,40,50#>步兵|');
INSERT INTO `zhcn` (`id`,`desc`) VALUES ('331009','【神武将】部分武将可以|<#225,40,50#>化神|，|<#225,40,50#>大幅|提升属性和战斗力，并获得普通武将所没有的|<#225,40,50#>专属技能|和|<#225,40,50#>马匹装备位||<#\n#>|【化神方法】拥有|<#225,40,50#>可化神武将|和|<#225,40,50#>对应神武将信物|，并且完成额外化神条件，即可在|<#225,40,50#>神龛|将该武将化神，获得神武将|<#\n#>|【神武将信物获得途径】在|<#225,40,50#>观星台|进行|<#225,40,50#>天陨|有概率随机获得，或通过收集完成|<#225,40,50#>六种御龙盔甲|后进行合成随机获得|<#\n#>|【御龙盔甲获得途径】通过|<#225,40,50#>限时比赛|、|<#225,40,50#>占星|、|<#225,40,50#>天陨|、|<#225,40,50#>剿匪|、|<#225,40,50#>锦囊商店|、|<#225,40,50#>联盟商店|等途径获得|<#\n#>|【马匹装备】神武将|<#225,40,50#>特有|的装备，可以通过攻打|<#225,40,50#>武将营寨|等途径获得|<#\n#>|【神武将升级】在世界地图上攻打|<#225,40,50#>匪徒/武将营寨|或者攻打|<#225,40,50#>其他玩家|对其造成一定战力损失后，有概率获得用于升级神武将等级的兵法残页、残简等道具|<#\n#>|【神武将技能升级】用|<#225,40,50#>混元丹|道具可以升级神武将技能');
